CREATE OR REPLACE FUNCTION find_cars_after_year(year_in INT) RETURNS TABLE ( model VARCHAR(255), year INT ) AS $$ BEGIN RETURN QUERY SELECT c.model, c.year FROM cars c WHERE c.year >= year_in ORDER BY c.year; END; $$ LANGUAGE plpgsql;
CREATE OR REPLACE PROCEDURE GET_TOTAL_CARS_BY_MODEL(IN model_in VARCHAR(50), OUT count_out INT) LANGUAGE plpgsql AS $$ BEGIN SELECT COUNT(*) INTO count_out FROM cars WHERE model LIKE '%' || model_in || '%'; END $$;

